mapper.select:
SELECT count(*)
FROM book_wait 
where date_format(book_date, '%Y/%m/%d') = #{date}

SELECT CONCAT(DATE_FORMAT(con_date, '%m/%d'), ' ', SUBSTR('일월화수목금토', DAYOFWEEK(con_date), 1)) as conDate
FROM book_wait_list
WHERE book_wait_idx = #{bwno}

select count(*)
from book_wait_list
where book_wait_idx = #{bwno}
